func Calculate_fuels_Avg(nums []int) int {
    num_crabs := len(nums);
    max := 0;
    avg := 0;
    for _, n := range nums {
        avg += n;
        if max < n {
            max = n;
        }
    }
    avg /= num_crabs;
    fuel := ((1+max)*max)/2 * len(nums);
    for i:=-1; i<2; i++ {
        cost := 0;
        for _, n := range nums {
            err := utils.Abs(avg+i-n);
            cost += ((1+err)*err)/2; // integer-div needs to be done last
        }
        if fuel > cost {
            fuel = cost;
        }
    }
    return fuel;
}
